var fs = require('fs');
var SQL = require('sql.js');
var path = require('path');
var events = require('events');

//Variables
sqlite.prototype.db = null;
sqlite.prototype.buffer = null;
sqlite.prototype.writer = null;
sqlite.prototype.file = null;
sqlite.prototype.sql = '';
sqlite.prototype.debug = false;

function sqlite () {
	
}

/**
   * Database connection
   *
   * @param {String|Object} db - File directory+filename | buffer
   * @return {Object}
   */
   sqlite.prototype.connect = function(db){
   	if(typeof(db)=='string'){
   		this.file = db;
		if (!db || db === ':memory:' || db.indexOf('file::memory:') === 0) {
			this.buffer = new Buffer(0);
		} else if(fs.existsSync(this.file)){
   			this.buffer = fs.readFileSync(this.file);
   		}
   	}else if(typeof(db)=="object"){
   		this.buffer = db;
   	}

   	if(this.buffer){
   		try{
   			this.db = new SQL.Database(this.buffer);	
   		}catch(x){
   			throw x;
   		}
   	}else{
   		try{
   			this.db = new SQL.Database();	
   			this.write();
   		}catch(x){
   			throw x;
   		}
   	}

   	return this;	
   }

/**
	* Alternative connection
	*/
	sqlite.prototype.con = sqlite.prototype.connect;

/**
   * Runing queries | Sync & Async
   *
   * @param {String} sql - SQL code
   * @param {Array|Function} options - Array to prepared sql | callback function
   * @param {Function} callback - callback function
   * @return {Array|Object}
   */
   sqlite.prototype.run = function(sql, options, callback) {
   	if(typeof(options) == "function"){
   		callback = options;
   		options = [];
   	}
   	var results;
   	var type = sql.substring(0,6);
   	type = type.toUpperCase();
   	switch(type){
   		case "SELECT": results = this.pvSELECT(sql, options); break;
   		case "INSERT": results = this.pvINSERT(sql, options); break;
   		case "UPDATE": results = this.pvUPDATE(sql, options); break;
   		case "DELETE": results = this.pvDELETE(sql, options); break;
   		case "PRAGMA": results = this.pvPRAGMA(sql, options); break;
   		default: results = this.runAll(sql)
   	}
   	if(callback){
   		callback(results);
   		return this;
   	}else{
   		return results;
   	}
   };

/**
   * Runing queries Async
   *
   * @param {String} sql - SQL code
   * @param {Array|Function} options - Array to prepared sql | callback function
   * @param {Function} callback - callback function
   * @return {Array|Object} 

   * @observation: This function will no longer be used soon!
   */
   sqlite.prototype.runAsync = function(sql, options, callback){
   	this.sql = sql;
   	if(typeof(options) == "function"){
   		options(this.run(sql));
   	}else{
   		callback(this.run(sql, options));
   	}
   	return this;
   }

/**
	* PRAGMA statements
	*
	* @param {String} sql - SQL statement
	* @param {Array} where - Array ti prepared sql
	* @return {Object}
	*/
	sqlite.prototype.pvPRAGMA = function(sql, where) {
		if((sql.split('=')).length>1){
		// update
		return this.pvUPDATE(sql, where);
	}else{
		// get
		return this.pvSELECT(sql, where);
	}
};

/**
   * Runing selects - PRIVATE
   *
   * @param {String}  sql - SQL code
   * @param {Array} where - Array to prepared sql 
   * @return {Object}
   */
   sqlite.prototype.pvSELECT = function(sql, where){
   	if(where){
   		for(var i = 0 ; i < where.length; i++){
   			sql = sql.replace('?',"\'"+where[i]+"\'");
   		}
   	}
   	this.sql = sql;
   	try{
   		var contents = this.db.exec(sql);	
   		if(contents.length){
   			var columns = contents[0].columns;
   			var values = contents[0].values;
   			var resultado = [];
   			for(var i = 0 ; i < values.length ; i++){
   				var linha = {};
   				for(var j = 0 ; j < columns.length; j++){
   					linha[columns[j]] = values[i][j]
   				}
   				resultado.push(linha);
   			}
   			return resultado;
   		}else{
   			return [];
   		}
   	}catch(x){
   		if(this.debug){
   			throw x;
   		}
   		return {error:x}
   	}
   }

/**
   * Runing deletes - PRIVATE
   *
   * @param {String}  sql - SQL code
   * @param {Array} where - Array to prepared sql 
   * @return {Boo}
   */
   sqlite.prototype.pvDELETE = function(sql, where){
   	if(where){
   		for(var i = 0 ; i < where.length; i++){
   			sql = sql.replace('?',where[i]);
   		}
   	}
   	this.sql = sql;
   	try{
   		this.db.exec(sql);	
